    private void typeChoosingActionPerformed(java.awt.event.ActionEvent evt) {
        if (typeChoosing.getSelectedItem().equals("SELLER"))
        {
            Institute_txt.setEditable(true);
            workShop_txt.setEditable(true);
            factory_txt.setEditable(true);
            forSellerLbl.setText("please fill int extra info");
            forSellerLbl.setForeground(Color.GREEN);
        }
        else
        {
            Institute_txt.setEditable(false);
            workShop_txt.setEditable(false);
            factory_txt.setEditable(false);
            forSellerLbl.setText("for seller !");
            forSellerLbl.setForeground(Color.RED);
        }
    }

