    private void buttonActionPerformed(java.awt.event.ActionEvent evt) throws wrongInputType{
        try {
            project.signingUp(name_txt.getText() ,family_txt.getText() ,email_txt.getText() ,phonrNum_txt.getText()
            , typeChoosing.getSelectedItem().toString() ,ID_txt.getText() , jPasswordField.getText() ,Institute_txt.getText()
            ,workShop_txt.getText() , factory_txt.getText());

            jLabel1.setText("added successfully !");
            jLabel1.setForeground(Color.green);
            this.dispose();

        }catch (AccessAllowance a){
            SwingUtilities.invokeLater(new Runnable() {
                @Override
                public void run() {
                    mainPage m=new mainPage();
                }
            });
                SwingUtilities.invokeLater(new Runnable() {
                    @Override
                    public void run() {
                        JFrame f=new JFrame();
                        JOptionPane.showMessageDialog(f,a.getMessage());
                    }
                });
            this.dispose();

        }
        catch (Exception e)
        {
            nameGiuden.setForeground(Color.RED);
            nameGiuden.setText(e.getMessage());
            SwingUtilities.invokeLater(new Runnable() {
                @Override
                public void run() {
                    JFrame f=new JFrame();
                    JOptionPane.showMessageDialog(f,e.getMessage());
                }
            });
            this.dispose();
        }
        //-----------------------

    }

